import React, { useState } from "react";
import CytoscapeComponent from 'react-cytoscapejs';
import Modal from "./Modal/Modal";


const SecondStage = () => {
    const [modalActive, setModalActive] = useState(Boolean)
    const [nodeSource, setNodeSource] = useState('');
    const [nodeTarget, setNodeTarget] = useState('');
    const [nodeLabel, setNodeLabel] = useState('');

    const [elements, setElement ] = useState([
        { data: { id: 'Радость', label: 'Радость' }, position: { x: 0, y: 0 } },
        { data: { id: 'Грусть', label: 'Грусть' }, position: { x: 100, y: 50 } },
        { data: { id: 'Злость', label: 'Злость' }, position: { x: 0, y: 100 } },
    ]);

    return <div className="col-md-8 mx-auto">

            <Modal active={modalActive} setActive={setModalActive}/>
                 <p className="w-100">
                    <h1 class="display-6">Этап 2: Задайте переходы между эмоциями</h1>
                </p>
                <div class="">
                    <CytoscapeComponent elements={elements} 
                style={ { 'width': '100%', 'height': '200px','background-color': '#f2f2f2'} }
                stylesheet={[
                    {
                      selector: 'node',
                      style: {
                        'width': 'label',
                        'height': 20,
                        'shape': 'round-rectangle',
                        'content': 'data(label)',
                        'text-valign': 'center',
                        'color': 'white',
                        'text-outline-width': 2,
                        'border-width': 12,
                        //'border-color': '#33BBD9',
                        'border-color': '#bbbbbb',
                        'text-outline-color': '#bbbbbb',
                        'background-color': '#bbbbbb',
                      }
                    },
                    {
                        selector: ':selected',
                        css: {
                        'background-color': 'SteelBlue',
                        'line-color': 'black',
                        'target-arrow-color': 'black',
                        'source-arrow-color': 'black'
                    }
                },
                    {
                        selector: 'highlight',
                        style: {
                          'width': 'label',
                          'height': 20,
                          'shape': 'round-rectangle',
                          'content': 'data(label)',
                          'text-valign': 'center',
                          'color': 'white',
                          'text-outline-width': 2,
                          'border-width': 12,
                          //'border-color': '#33BBD9',
                          'border-color': '#33BBD9',
                          'text-outline-color': '#33BBD9',
                          'background-color': '#33BBD9',
                        }
                      },
                    {
                      selector: 'edge',
                      style: {
                        'width': 2,
                        'target-arrow-shape': 'triangle',
                        'line-color': '#aaaaaa',
                        'target-arrow-color': '#aaaaaa',
                        'curve-style': 'bezier',
                        'content': 'data(label)',
                        'text-valign': 'top',
                        'text-margin-y': -12,
                        'color': '#212121',
                      }
                    }
                    ]}/>
                </div>

                <div>
                <form className='' >
                    <div class="form-group">
                        <label for="formGroupExampleInput">Начальное эмоциональное состояние</label>
                        <select class="form-select" name="target"  onChange={(e) => {
                            let newCollection = ''
                            newCollection =  e.target.value
                            e.preventDefault();
                            console.log(nodeLabel);


                        }}>
                            <option selected disabled>Выберите эмоцию</option>
                            <option value="Радость">Радость</option>
                            <option value="Грусть">Грусть</option>
                            <option value="Злость">Злость</option>
                        </select>      
                    </div>
                    <div class="">
                        <label for="formGroupExampleInput">Количество ответов</label>
                        <input type="text" class="form-control" id="formGroupExampleInput" placeholder="Введите значение" onChange={(e) => {
                            let newCollection = ''
                            newCollection =  e.target.value
                            setNodeLabel(newCollection)
                        }}/>
                    </div>
                    <div class="form-group">
                        <label for="formGroupExampleInput2">Неправильные</label>
                        <input class="form-check-input" type="radio" id="flexSwitchCheckChecked" />
                        <label for="formGroupExampleInput2">Правильные</label>
                        <input class="form-check-input" type="radio" id="flexSwitchCheckChecked" />           
                    </div>
                    <div class="form-group col-md-2">
                        <label for="formGroupExampleInput">Откуда</label>
                        <select class="form-select" name="target"  onChange={(e) => {
                            let newCollection = ''
                            newCollection =  e.target.value
                            setNodeSource(newCollection)
                        }}>
                            <option selected disabled>Выберите эмоцию</option>
                            <option value="Радость">Радость</option>
                            <option value="Грусть">Грусть</option>
                            <option value="Злость">Злость</option>
                        </select>

                        <label for="formGroupExampleInput">Куда</label>
                        <select class="form-select" onChange={(e) => {
                            let newCollection = ''
                            newCollection =  e.target.value
                            setNodeTarget(newCollection)
                        }}>
                            <option selected disabled>Выберите эмоцию</option>
                            <option value="Радость">Радость</option>
                            <option value="Грусть">Грусть</option>
                            <option value="Злость">Злость</option>
                        </select>
                    </div>
                    <div class="form-group">


                    </div>
                    <div class="form-group">

                    </div>
                </form>

                </div>


                <nav class="navbar fixed-bottom navbar-light bg-light" >
                    <form class="col-md-8 mx-auto">

                        <button type='button' class="btn btn-outline-primary me-2" onClick={e => {

                            }}>
                            Назад
                        </button>

                        <button type='button' class="btn btn-primary me-2" onClick={() => {
                           CytoscapeComponent.addClass('highlight');
                        }}>
                            Начальная эмоция
                        </button>
                        <button type="button" class="btn btn-primary me-2" onClick={(e) => {

                            let newCollection = [...elements]
                            newCollection.push({ data: { source: nodeSource, target: nodeTarget, label: nodeLabel} })
                            setElement(newCollection)

                            e.preventDefault();
                            console.log(nodeLabel);
                            console.log(nodeSource);
                            console.log(nodeTarget);
                        }}>Добавить ребро</button> 

                        <button type='button' class="btn btn-outline-primary" onClick={(e) => {

                        }}>
                            Далее
                        </button>
                    </form>
