import React, { useState } from "react";
import cytoscape from 'cytoscape';
import edgehandles from 'cytoscape-edgehandles';


const GenerateScormPage = () => {

    let cy = cytoscape({

        container: document.getElementById('cy'), // container to render in

        elements: [ // list of graph elements to start with
                    // { group: 'nodes',data: { id: 'n1' }, position: { x: 200, y: 100 } },
                    // { group: 'nodes',data: { id: 'n2' }, position: { x: 100, y: 50 } },
                    // { group: 'edges',data: { id: 'e0', source: 'n1', target: 'n2' } }
                ],

        style: [ // the stylesheet for the graph
        {
        selector: 'node',
        style: {
            'background-color': '#69e',
            'label': 'data(id)',
            }
        },

        {
        selector: 'edge',
        style: {
            'width': 1,
            'line-color': '#369',
            'target-arrow-color': '#369',
            'target-arrow-shape': 'triangle',
            'label': 'data(label)',
            'font-size': '14px',
            'color': '#777'
                }
        }
        ],

style: cytoscape.stylesheet()
.selector('edge')
.css({
  'width': 3,
  'line-color': '#369',
  'target-arrow-color': '#369',
  'target-arrow-shape': 'triangle',
  'label': 'data(label)',
  'font-size': '14px',
  'color': '#777'
})
.selector('node')
.css({
  'content': 'data(id)',
  'text-valign': 'center',
  'color': 'white',
  'text-outline-width': 2,
  'text-outline-color': '#888',
  'background-color': '#888'
})
.selector(':selected')
.css({
  'background-color': 'black',
  'line-color': 'black',
  'target-arrow-color': 'black',
  'source-arrow-color': 'black',
  'text-outline-color': 'black'
}),

layout: {
name: 'grid',
rows: 1
}

});

cy.add([
{ group: 'nodes',data: { id: '1', name:'n11' }, position: { x: 50, y: 200 } },
{ group: 'nodes',data: { id: '2' }, position: { x: 131, y: 226 } },
{ group: 'nodes',data: { id: '3' }, position: { x: 128, y: 143 } },

//{ group: 'edges',data: { id: 'e0', source: 'Грусть', target: 'Радость', label: 7 } },
//{ group: 'edges',data: { id: 'e1', source: 'Радость', target: 'Злость', label: 10 } },

//{ group: 'edges',data: { id: 'e3', source: 'Грусть', target: 'Злость', label: 9 } },

]);
cy.on('click', 'node', function(evt){
var node = evt.target; 
console.clear()
console.log( node.position() );
});
    const [questionList, setQuestionList] = useState([{ question: '', answers: [{ answer: '', isValid: false }] }]);

    const addNewQuestion = () => {
        questionList.push({ question: '', answers: [{ answer: '', isValid: false }] });
        setQuestionList(questionList);
    }

    const generateScorm = (e) => {
        e.preventDefault();
        debugger;
        console.log(JSON.stringify(questionList));
    }

    return <div className="d-flex flex-column">

        <p className="w-100">
            Generate SCORM
        </p>
        <p>
            Кол-во вопросов: {questionList.length}
        </p>
        <div className="d-flex flex-column">
            <form className='d-flex flex-column' onSubmit={(e) => generateScorm(e)}>
                {
                    questionList.map((questionItem, questionIndex) => {
                        return <React.Fragment key={questionIndex}>
                            <div className="d-flex flex-column">
                                <div className='d-flex flex-row'>
                                    <h1>Вопрос №{questionIndex + 1}</h1>
                                </div>
                                <input type="text" placeholder={`Вопрос ${questionIndex + 1}`} onChange={(e) => {
                                    let newCollection = [...questionList];
                                    newCollection[questionIndex].question = e.target.value;
                                    setQuestionList(newCollection);
                                }} />
                            </div>
                            <p className='mt-2 mb-1'>
                                Ответы
                            </p>
                            {
                                questionItem.answers.map((answerItem, answerIndex) => {
                                    return <React.Fragment key={answerIndex}>
                                        <input type="text" placeholder={`Ответ ${answerIndex + 1}`} onChange={(e) => {
                                            let newCollection = [...questionList];
                                            newCollection[questionIndex].answers[answerIndex].answer = e.target.value;
                                            setQuestionList(newCollection);
                                        }} />
                                        <label>
                                            <input
                                                type="checkbox"
                                                onChange={(e) => {
                                                    let newCollection = [...questionList];
                                                    newCollection[questionIndex].answers[answerIndex].isValid = e.target.checked;
                                                    setQuestionList(newCollection);
                                                }}
                                                // name={key}
                                                checked={answerItem.isValid}
                                            />
                                            {`${(answerItem.isValid) ? 'Правильный' : 'Неправильный'}`}
                                        </label>
                                    </React.Fragment>
                                })
                            }
                            <div className='d-flex flex-row mx-auto mx-2'>
                            <button className='btn btn-outline-primary' type="button" onClick={() => {
                                let newCollection = [...questionList];
                                newCollection[questionIndex].answers.pop();
                                setQuestionList(newCollection);
                            }}>
                                Удалить ответ
                            </button>
                            <button className='btn btn-primary mx-2' type="button" onClick={() => {
                                let newCollection = [...questionList];
                                newCollection[questionIndex].answers.push({ answer: '', isValid: false });
                                setQuestionList(newCollection);
                            }}>
                                Добавить ответ
                            </button>
                            </div>
                            <hr />
                        </React.Fragment>
                    })
                }
                <div className='d-flex flex-row mx-auto'>


                    <button className='btn btn-outline-primary' onClick={(e) => {
                        let newCollection = [...questionList];
                        newCollection.pop();
                        setQuestionList(newCollection);
                    }}>
                        Удалить последний вопрос
                    </button>
                    <button type='button' className="btn btn-primary mx-2" onClick={e => {
                        let newCollection = [...questionList];
                        newCollection.push({ question: '', answers: [{ answer: '123', isValid: false }] })
                        setQuestionList(newCollection);
                    }}>
                        Добавить новый вопрос
                    </button>
                    <button type='submit' className="btn btn-success">
                        Сгенерировать SCORM пакет
                    </button>
                </div>
            </form>
        </div>
        <div id="cy">


        </div>

    </div>
}

export default GenerateScormPage;
