                    {
                      selector: 'node',
                      style: {
                        width: 'label',
                        height: 30,
                        shape: 'round-rectangle',
                        'content': 'data(label)',
                        'text-valign': 'center',
                        'color': 'white',
                        'text-outline-width': 2,
                        'border-width': 8,
                        'border-color': '#3BCFDF',
                        'text-outline-color': '#3BCFDF',
