import React, { useState } from "react";
import CytoscapeComponent from 'react-cytoscapejs';
import Modal from "./Modal/Modal";


const SecondStage = () => {
    const [modalActive, setModalActive] = useState(Boolean)
    const [nodeSource, setNodeSource] = useState('');
    const [nodeTarget, setNodeTarget] = useState('');
    const [nodeLabel, setNodeLabel] = useState('');

    const [elements, setElement ] = useState([
        { data: { id: 'Радость', label: 'Радость' }, position: { x: 0, y: 0 } },
        { data: { id: 'Грусть', label: 'Грусть' }, position: { x: 100, y: 50 } },
        { data: { id: 'Злость', label: 'Злость' }, position: { x: 0, y: 100 } },
    ]);

    return <div className="d-flex flex-column mx-auto">
            <Modal active={modalActive} setActive={setModalActive}/>
                 <p className="w-100">
                    <h1 class="display-6">Этап 2: Задайте переходы между эмоциями</h1>
                </p>
                <div>
                    <input type="checkbox" checked data-toggle="toggle"/>
                    <form class="input-group me" >
                        <div class="btn-group" role="group" aria-label="Basic example">
                            <button type="button" class="btn btn-secondary">КОЛИЧЕСТВО ОТВЕТОВ</button>
                            <button type="button" class="btn btn-secondary">ПРОЦЕНТ ОТВЕТОВ</button>
                        </div>

                        <input value="Количество ответов" class="form-control " type="text" name="label" onChange={(e) => {
                            let newCollection = ''
                            newCollection =  e.target.value
                            setNodeLabel(newCollection)
                        }}></input>
                        <div class="form-check form-switch">
                            <input class="form-check-input" type="checkbox" id="flexSwitchCheckDefault"/>
                            <label class="form-check-label" for="flexSwitchCheckDefault">Правильно</label>
                        </div>
                        <label>Откуда</label>
                        <div lass="form-group">
                        <select class="form-control" name="target"  onChange={(e) => {
                            let newCollection = ''
                            newCollection =  e.target.value
                            setNodeSource(newCollection)
                        }}>
                            <option selected disabled>Выберите эмоцию</option>
                            <option value="Радость">Радость</option>
                            <option value="Грусть">Грусть</option>
                            <option value="Злость">Злость</option>
                        </select>
                        </div>

                        <label>Куда</label>
                        <select class="form-control" onChange={(e) => {
                            let newCollection = ''
                            newCollection =  e.target.value
                            setNodeTarget(newCollection)
                        }}>
                            <option selected disabled>Выберите эмоцию</option>
                            <option value="Радость">Радость</option>
                            <option value="Грусть">Грусть</option>
                            <option value="Злость">Злость</option>
                        </select>
                        <div>

                        </div>

                        <button type="submit" class=" form-control btn btn-primary" onClick={(e) => {

                            let newCollection = [...elements]
                            newCollection.push({ data: { source: nodeSource, target: nodeTarget, label: nodeLabel} })
                            setElement(newCollection)

                            e.preventDefault();
                            console.log(nodeLabel);
                            console.log(nodeSource);
                            console.log(nodeTarget);
                        }}>Добавить ребро</button>
                    </form>
                </div>

               <CytoscapeComponent elements={elements} 
                style={ { 'width': '100%', 'height': '500px','background-color': '#f2f2f2' } }
                stylesheet={[
                    {
                      selector: 'node',
                      style: {
                        'width': 'label',
                        'height': 20,
                        'shape': 'round-rectangle',
                        'content': 'data(label)',
                        'text-valign': 'center',
                        'color': 'white',
                        'text-outline-width': 2,
                        'border-width': 12,
                        'border-color': '#33BBD9',
                        'text-outline-color': '#33BBD9',
                        'background-color': '#33BBD9',
                      }
                    },
                    {
                      selector: 'edge',
                      style: {
                        'width': 2,
                        'target-arrow-shape': 'triangle',
                        'line-color': '#aaaaaa',
                        'target-arrow-color': '#aaaaaa',
                        'curve-style': 'bezier',
                        'content': 'data(label)',
                        'text-valign': 'top',
                        'text-margin-y': -12,
                        'color': '#212121',
                      }
                    }
                  ]}


                />;
                <nav class="navbar fixed-bottom navbar-light bg-light">
                    <form class="col-md-8 mx-auto">

                        <button type='button' class="btn btn-primary me-2" onClick={e => {

                            }}>
                            Назад
                        </button>
                        <button type="button" class="btn btn-outline-primary me-2"  onClick={(e) => {

                            }}>
                            Удалить
                        </button>

                        <button type='button' class="btn btn-primary me-2" onClick={() => setModalActive(true)}>
                            Добавить
                        </button>

                        <button type='button' class="btn btn-primary" onClick={(e) => {

                        }}>
                            Далее
                        </button>
                    </form>
                    <div>
                    </div>
                </nav>

            </div>




}
export default SecondStage;
