import React, { useState } from "react";
import CytoscapeComponent from 'react-cytoscapejs';

const SecondStage = () => {
    const [elements, setElement ] = useState([
        { data: { id: 'Радость', label: 'Радость' }, position: { x: 0, y: 0 } },
        { data: { id: 'Грусть', label: 'Грусть' }, position: { x: 100, y: 0 } },
        { data: { id: 'Злость', label: 'Злость' }, position: { x: 100, y: 100 } },
        { data: { source: 'Радость', target: 'Грусть', label: "" } }
    ]
    );
    const styleSheet = [
        {
          selector: "node",
          style: {
            backgroundColor: "#4a56a6",
            width: 30,
            height: 30,
            label: "data(label)",

            // "width": "mapData(score, 0, 0.006769776522008331, 20, 60)",
            // "height": "mapData(score, 0, 0.006769776522008331, 20, 60)",
            // "text-valign": "center",
            // "text-halign": "center",
            "overlay-padding": "6px",
            "z-index": "10",
            //text props
            "text-outline-color": "#4a56a6",
            "text-outline-width": "2px",
            color: "white",
            fontSize: 20
          }
        },
        {
          selector: "node:selected",
          style: {
            "border-width": "6px",
            "border-color": "#AAD8FF",
            "border-opacity": "0.5",
            "background-color": "#77828C",
            width: 50,
            height: 50,
            //text props
            "text-outline-color": "#77828C",
            "text-outline-width": 8
          }
        },
        {
          selector: "node[type='device']",
          style: {
            shape: "rectangle"
          }
        },
        {
          selector: "edge",
          style: {
            width: 3,
            // "line-color": "#6774cb",
            "line-color": "#AAD8FF",
            "target-arrow-color": "#6774cb",
            "target-arrow-shape": "triangle",
            "curve-style": "bezier"
          }
        }
      ];
    return <div className="d-flex flex-column">
                 <p className="w-100">
                    <h1 class="display-6">Этап 2: Задайте переходы между эмоциями</h1>
                </p>
                <div>
                    <form>
                        <label>Количество ответов</label>
                        <input type="text" name="label" ></input>
                        <label>Откуда</label>
                        <input type="text" name="source" ></input>
                        <label>Куда</label>
                        <input type="text" name="target" ></input>
                        <button type="submit" class="btn btn-primary" >Добавить ребро</button>
                    </form>
                </div>

                <CytoscapeComponent elements={elements} 
                style={ { 'width': '80%', 'height': '500px','background-color': '#f2f2f2' } }

                />;
                <nav class="navbar fixed-bottom navbar-light bg-light">
                    <form class="col-md-8 mx-auto">

                        <button type='button' class="btn btn-primary me-2" onClick={e => {

                            }}>
                            Назад
                        </button>
                        <button type="button" class="btn btn-outline-primary me-2"  onClick={(e) => {

                            }}>
                            Удалить
                        </button>

                        <button type='button' class="btn btn-primary me-2" onClick={(e) => {
                            elements.add([
                                { data: { source: 'two', target: 'three', label: 4 } },
                            ])
                        }}>
                            Добавить
                        </button>

                        <button type='button' class="btn btn-primary" onClick={(e) => {

                        }}>
                            Далее
                        </button>
                    </form>
                    <div>
                    </div>
                </nav>

            </div>



}
export default SecondStage;
