import React, { useState } from "react";
import cytoscape from 'cytoscape';
import edgehandles from 'cytoscape-edgehandles';


const GenerateScormPage = () => {
    let cy = cytoscape({

        container: document.getElementById('cy'), // container to render in

        elements: [ ],

        style: [ 
        {
        selector: 'node',
        style: {
            'background-color': '#69e',
            'label': 'data(id)',
            }
        },
        {
        selector: 'edge',
        style: {
            'width': 1,
            'line-color': '#369',
            'target-arrow-color': '#369',
            'target-arrow-shape': 'triangle',
            'label': 'data(label)',
            'font-size': '14px',
            'color': '#777'
                }
        }
        ],
        style: cytoscape.stylesheet()
        .selector('edge')
        .css({
        'width': 3,
        'line-color': '#369',
        'target-arrow-color': '#369',
        'target-arrow-shape': 'triangle',
        'label': 'data(label)',
        'font-size': '14px',
        'color': '#777'
        })
        .selector('node')
        .css({
        'content': 'data(id)',
        'text-valign': 'center',
        'color': 'white',
        'text-outline-width': 2,
        'text-outline-color': '#888',
        'background-color': '#888'
        })
        .selector(':selected')
        .css({
        'background-color': 'black',
        'line-color': 'black',
        'target-arrow-color': 'black',
        'source-arrow-color': 'black',
        'text-outline-color': 'black'
        }),

        layout: {
            name: 'grid',
            rows: 1
        }
        });

        cy.add([
            { group: 'nodes',data: { id: '1', name:'n11' }, position: { x: 50, y: 200 } },
            { group: 'nodes',data: { id: '2' }, position: { x: 131, y: 226 } },
            { group: 'nodes',data: { id: '3' }, position: { x: 128, y: 143 } },
        //{ group: 'edges',data: { id: 'e0', source: 'Грусть', target: 'Радость', label: 7 } },
        ]);
        cy.on('click', 'node', function(evt){
        var node = evt.target; 
        console.clear()
        console.log( node.position() );
        });
    const [questionList, setQuestionList] = useState([{ question: '', answers: [{ answer: '', isValid: false }] }]);

    const addNewQuestion = () => {
        questionList.push({ question: '', answers: [{ answer: '', isValid: false }] });
        setQuestionList(questionList);
    }

    const generateScorm = (e) => {
        e.preventDefault();
        debugger;
        console.log(JSON.stringify(questionList));
    }

    return <div className="d-flex flex-column">

        <p className="w-100">
            Generate SCORM
        </p>
        <p>
            Кол-во вопросов: {questionList.length}
        </p>


        <div id="cy">       
        </div>

    </div>
}

export default GenerateScormPage;
