import React, { useState } from "react";
import CytoscapeComponent from 'react-cytoscapejs';


const SecondStage = () => {
    const [nodeSource, setNodeSource] = useState('');
    const [nodeTarget, setNodeTarget] = useState('');
    const [nodeLabel, setNodeLabel] = useState('');

    const [elements, setElement ] = useState([
        { data: { id: 'Радость', label: 'Радость' }, position: { x: 0, y: 0 } },
        { data: { id: 'Грусть', label: 'Грусть' }, position: { x: 100, y: 0 } },
        { data: { id: 'Злость', label: 'Злость' }, position: { x: 100, y: 100 } },
        { data: { source: 'Радость', target: 'Грусть', label: "" } }
    ]);

    const addNode = () => {
        elements.push({ data: { source: nodeSource, target: nodeTarget, label: nodeLabel} })
        setElement(elements);

      }


    return <div className="d-flex flex-column">
                 <p className="w-100">
                    <h1 class="display-6">Этап 2: Задайте переходы между эмоциями</h1>
                </p>
                <div>
                    <form>
                        <label>Количество ответов</label>
                        <input type="text" name="label" onChange={(e) => {
                            let newCollection = [...elements]
                            newCollection.source =  e.target.value
                            setNodeLabel(newCollection)
                        }}></input>
                        <label>Откуда</label>
                        <input type="text" name="source" value={nodeSource} onChange={(event) => setNodeSource(event.target.value)} ></input>
                        <label>Куда</label>
                        <input type="text" name="target" value={nodeTarget} onChange={(event) => setNodeTarget(event.target.value)}></input>
                        <button type="submit" class="btn btn-primary" onClick={addNode()}>Добавить ребро</button>
                    </form>
                </div>

                <CytoscapeComponent elements={elements} 
                style={ { 'width': '80%', 'height': '500px','background-color': '#f2f2f2' } }

                />;
                <nav class="navbar fixed-bottom navbar-light bg-light">
                    <form class="col-md-8 mx-auto">

                        <button type='button' class="btn btn-primary me-2" onClick={e => {

                            }}>
                            Назад
                        </button>
                        <button type="button" class="btn btn-outline-primary me-2"  onClick={(e) => {

                            }}>
                            Удалить
                        </button>

                        <button type='button' class="btn btn-primary me-2" onClick={(e) => {
                            elements.add([
                                { data: { source: 'two', target: 'three', label: 4 } },
                            ])
                        }}>
                            Добавить
                        </button>

                        <button type='button' class="btn btn-primary" onClick={(e) => {

                        }}>
                            Далее
                        </button>
                    </form>
                    <div>
                    </div>
                </nav>

            </div>



}
export default SecondStage;
